// Add your own function to filter the fields
add_filter( 'job_manager_job_dashboard_columns', 'custom_dashboard' );

// This is your function which takes the fields, modifies them, and returns them
// You can see the fields which can be changed here: https://github.com/mikejolley/WP-Job-Manager/blob/master/includes/forms/class-wp-job-manager-form-submit-job.php
function custom_dashboard( $job_dashboard_columns ) {

    // Here we target one of the job fields (job_title) and change it's label
    
    $job_dashboard_columns['filled'] = "Closed?";
  	//$job_dashboard_columns['expires']=get_the_project_deadline();
	$job_dashboard_columns['expires'] = "Deadline";

    // And return the modified fields
    return $job_dashboard_columns;
}
